import React, { useState, useEffect } from "react";
import { View, Tetxt, FlatList, TouchableOpacity, Alert } from 'react-native';
import { useNavigation } from "@react-navigation/native";

const GalleryScreen = () => {
    const navigation = useNavigation();
    const [images, setImages] = useState([
        { id: 1, src: require('./images/image1.jpg'), name: "img1" },
        { id: 2, src: require('./images/image2.jpg'), name: "img2" },
        { id: 3, src: require('./images/image3.jpg'), name: "img3" },
    ]);

    const handleLongPress = (image) => {
        Alert.alert('Выберите действие', '', [
            { text: 'Отмена', onPress: () => console.log('отменено') },
            { text: 'Удалить', onPress: () => handleDelete(image.id) },
            { text: 'Переименовать', onPress: () => handleRename(image.id) },
        ],
            { cancelable: true }
        )
    }

    const handleDelete = (id) => {
        setImages((prevImages) => prevImages.filter((image) => image.id !== id));
    }

    const handleRename = (id) => {

    };

    const renderItem = ({ item }) => (
        <TouchableOpacity onLongPress={() => handleLongPress(item)}>
            <View>
                <Text>{item.name}</Text>
                <Image source={item.src} style={{ width: 100, height: 100 }} />
            </View>
        </TouchableOpacity>
    );

    return (
        <View>
            <FlatList data={images}
                renderItem={renderItem}
                keyExtractor={(item) => item.id.toString()} />
        </View>
    )

}

export default GalleryScreen;
