import React, { useState } from 'react';
import { View, Switch, Pressable, StyleSheet, SectionList, Image, FlatList } from 'react-native';
import { Text } from 'react-native-paper';
import { Provider, useSelector, useDispatch } from 'react-redux';
import { store } from './redux/store';
import { fetchIMG } from './redux/imageSlice';

// Подключаем Redux в App
const App = () => {
  return (<Provider store={store}>
    <Container />
  </Provider>);
}

const Container = () => {

  const img = useSelector((state) => state.image.image);

  // Подключаем dispatch
  const dispatch = useDispatch();

  return (
    <View style={{flex:1, alignItems: 'center', justifyContent: 'center'}}>
      <Pressable onPress={() => { dispatch(fetchIMG()) }}>
        <Text style={{fontSize:20}}>Random dog image</Text>
      </Pressable>

      <Text>{img}</Text>

      <Image
        source={{ uri: img }}
        style={{ width: 500, height: 500 }}
      />

    </View>
  );
}

export default App;
