// BottomTabNavigator.js

import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { FontAwesome } from '@expo/vector-icons'; // Импортируйте иконки FontAwesome

import RoutesScreen from './RoutesScreen';
import MapsScreen from './MapsScreen';
import PaymentsScreen from './PaymentsScreen';
import MenuScreen from './MenuScreen';

const Tab = createBottomTabNavigator();

const BottomTabNavigator = () => {
  return (
    <Tab.Navigator
      tabBarOptions={{
        activeTintColor: 'green', // Цвет активной иконки
      }}
    >
      <Tab.Screen
        name="Маршруты"
        component={RoutesScreen}
        options={{
          tabBarIcon: ({ color, size }) => (
            <FontAwesome name="road" color={color} size={size} />
          ),
        }}
      />
      <Tab.Screen
        name="Карты"
        component={MapsScreen}
        options={{
          tabBarIcon: ({ color, size }) => (
            <FontAwesome name="map" color={color} size={size} />
          ),
        }}
      />
      <Tab.Screen
        name="Платежи"
        component={PaymentsScreen}
        options={{
          tabBarIcon: ({ color, size }) => (
            <FontAwesome name="dollar" color={color} size={size} />
          ),
        }}
      />
      <Tab.Screen
        name="Меню"
        component={MenuScreen}
        options={{
          tabBarIcon: ({ color, size }) => (
            <FontAwesome name="bars" color={color} size={size} />
          ),
        }}
      />
    </Tab.Navigator>
  );
};

export default BottomTabNavigator;
