import React, { useState } from 'react';
import { View } from 'react-native';
import {
  MD3LightTheme as DefaultTheme,
  PaperProvider, Card, Text
} from 'react-native-paper';
import books from './books';
import { FlatList } from 'react-native-web';

const App = () => {

  const [expandedBooks, setExpandedBooks] = useState([]);

  const toggleBookExpansion = (bookId) => {
    if (expandedBooks.includes(bookId)) {
      setExpandedBooks(expandedBooks.filter(id => id !== bookId));
    } else {
      setExpandedBooks([...expandedBooks, bookId]);
    }
    console.log(bookId);
  }

  const renderer = ({ item }) => {
    const isExpanded = expandedBooks.includes(item.id);
    // console.log(item);
    // console.log(item.id);


    return (
      <Card>
        <Card.Title title={item.title} onPress={() => toggleBookExpansion(item.id)}></Card.Title>
        <Card.Content>
          {isExpanded && (
            <View>
              <Text>Артикул: {item.id}</Text>
              <Text>Авторы: {item.authors}</Text>
            </View>
          )}

        </Card.Content>
      </Card>

    )
  }

  return (
    <PaperProvider theme={theme}>
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <FlatList
          data={books.books}
          renderItem={renderer}
          keyExtractor={(item, index) => item + index}
        >
        </FlatList>
      </View>
    </PaperProvider>
  );
}

const theme = {
  ...DefaultTheme
}

export default App;
