import React, {useState} from 'react';
import {View,Text, Switch, StyleSheet, ImageBackground, ActivityIndicator, TouchableWithoutFeedback} from 'react-native';

const App = () => {
  const [isEnabled, setIsEnabled] = useState(false);
  const toggleSwitch = () => setIsEnabled(previousState => !previousState)
  const [isLoading, setIsLoading] = useState(false);
  const [info, setInfo] = useState("");


  return (
    <View style={styles.container}>
      <ImageBackground source={{uri:"https://nasled.su/media/k2/items/cache/7af0193b8351bd1eb04c09dfe5731fa3_XL.jpg"}} style={{width:'100vh', height:"100vh"}} >

      <Switch
        trackColor={{false: '#ff8a8a', true: '#81b5e7'}}
        thumbColor={isEnabled ? '#0582af' : '#d60000'}
        onValueChange={toggleSwitch}
        value={isEnabled}
        style={{marginHorizontal: "auto"}}
      />
      <ActivityIndicator
        animating={isLoading}
        size={'large'}
        color={"green"}
      >
      </ActivityIndicator>

      <TouchableWithoutFeedback onPress={() => {setInfo("pressed on btn")}}><Text>Нажми на мене</Text>
      </TouchableWithoutFeedback>

      <Text>{info}</Text>


      </ImageBackground>

    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default App;
